<template>
  <img :src="realSrc">
</template>
<script>
import { imgUrl } from '@/config/env'
const base64Url = 'data:image/gif;base64,'
export default {
  name: 'IepImg',
  inheritAttrs: false,
  props: {
    src: {
      required: true,
      validator: prop => typeof prop === 'string' || prop === null || prop === undefined,
    },
  },
  computed: {
    realSrc () {
      if (!this.src) {
        return base64Url
      }
      return imgUrl + this.src
    },
  },
}
</script>
